library("extrafont")
extrafont::loadfonts(device="win")
library(ggplot2)
library(readr)
library(dplyr)
library(gridExtra)


stationtemp<-read_csv("C:/Users/valexandre/Downloads/TG_STAID000203.txt", 
                   skip = 19)
stationprecip<-read_csv("C:/Users/valexandre/Downloads/RR_STAID000203.txt", 
                      skip = 19)

stationtemp$moisjour<-substr(stationtemp$DATE,start = 5, stop = 8)
stationtemp<-subset(stationtemp, as.numeric(substr(stationtemp$DATE,start = 1,stop = 4)) >1980)
stationtemp<-stationtemp%>%
  filter(TG != -9999 )%>%
  filter(Q_TG == 0 )

stationprecip$moisjour<-substr(stationprecip$DATE,start = 5, stop = 8)
stationprecip<-subset(stationprecip, as.numeric(substr(stationprecip$DATE,start = 1,stop = 4)) >1980)
stationprecip<-stationprecip%>%
  filter(RR != -9999 )%>%
  filter(Q_RR == 0 )

df<-stationtemp%>%
  group_by(moisjour)%>%
  summarise(moyenne=mean(TG))
df$mois<-substr(df$moisjour,start = 1, stop = 2)
df$jour<-substr(df$moisjour,start = 3, stop = 4)


df2<-stationprecip%>%
