datapolystation$joursemaine<-rep(c(rep(c(rep("L",3),rep("M",3),rep("M",3),rep("J",3),rep("V",3),rep("S",3),rep("D",3)),52),rep("L",3)),2)
graphstation <- ggplot(datapolystation, aes(x = x, y = y)) +
  geom_polygon(aes(fill = what, alpha=0.5))+
  facet_grid(mois~jour,labeller=plot_labeller, drop=TRUE)+
  scale_fill_manual(values = c("#DB4747",  "#4750DB"))
graphstationfinaljpeg<-graphstation+
  theme_void()+theme(panel.border = element_blank(),
        panel.grid.major.x = element_blank(),
        panel.grid.minor.x = element_blank(),
  strip.background = element_blank(),
  strip.text = element_text(family="Josefin Sans", size=36, hjust=0),
  axis.text.x = element_blank(),axis.ticks.y = element_blank(),
        axis.text.y = element_blank(),axis.ticks.x = element_blank(),
        axis.title.y = element_blank(), axis.title.x = element_blank(),
        legend.position="none",
  plot.title = element_text(hjust = 0.5, family="Playfair Display", size=50, vjust=1),
  plot.caption = element_text(hjust = 1, family="Playfair Display", size=34))+
  geom_text(aes(x=90,y=5, label=joursemaine, colour=joursemaine), family="Playfair Display", size=8)+
  scale_color_manual(values = c("S"= "#141E28", "D"="#141E28",
                                "L"= "#CCCCCC", "M"= "#CCCCCC", "J"= "#CCCCCC","V"= "#CCCCCC"))+
  labs(title = "Températures et précipitations moyennes à Luxembourg")+ 
  labs(caption = "Données European Climate Assessment & Dataset, moyennes quotidiennes depuis 1981, calendrier @humeursdevictor")
  

jpeg(filename = "calendrierluxembourg.jpg", width=3*1000, height = 3*760, quality=100, units = "px",type="cairo")
graphstationfinaljpeg
dev.off()

ggsave("calendrier.jpeg", 
       width = 100, height = 76, units = "cm")
